const mergeThemes = (baseTheme, ...themes) => {
  if (themes) {
    return themes.reduce((acc, theme) => {
      if (typeof theme === 'function') {
        theme = theme(baseTheme);
      }

      return Object.assign({}, acc, theme);
    }, baseTheme);
  }

  return baseTheme;
};

export default mergeThemes;
