import React from 'react';
import PropTypes from 'prop-types';
import { createStyledComponent } from '@cloudflare/style-container';

const styles = ({ theme }) => ({
  my: 2,
  mx: 0,

  borderTop: `1px solid ${theme.colors.gray[7]}`
});

const DropdownSeparator = ({ className }) => (
  <div className={className} role="separator" />
);

DropdownSeparator.propTypes = {
  className: PropTypes.string
};

DropdownSeparator.displayName = 'DropdownSeparator';
export default createStyledComponent(styles, DropdownSeparator);
