import React from 'react';
import PropTypes from 'prop-types';

import SVG_COMPONENTS from './reactsvgs';
import iconTypes from './iconTypes';
import { createComponent } from '@cloudflare/style-container';

const DEFAULT_HEIGHT = 15;

const getHeight = size => {
  let height;
  switch (size) {
    case '1.2x':
      height = 18;
      break;
    case '1.5x':
      height = 22;
      break;
    case '2x':
      height = 30;
      break;
    case '2.5x':
      height = 37;
      break;
    case '3x':
      height = 45;
      break;
    case '3.5x':
      height = 52;
      break;
    case '4x':
      height = 60;
      break;
    default:
      height = DEFAULT_HEIGHT;
  }
  return height;
};

const getFill = (color, theme) => {
  let fill;
  switch (color) {
    case 'default':
      fill = theme.colors.gray[4];
      break;
    case 'primary':
      fill = theme.colors.blue[4];
      break;
    case 'success':
      fill = theme.colors.green[6];
      break;
    case 'warning':
      fill = theme.colors.orange[5];
      break;
    case 'danger':
      fill = theme.colors.red[3];
      break;
    case 'black':
      fill = theme.colors.black;
      break;
    case 'white':
      fill = theme.colors.white;
      break;
    default:
      fill = theme.colors.black;
  }
  return fill;
};

const iconStyles = ({ theme, size, color }) => ({
  height: size ? getHeight(size) : DEFAULT_HEIGHT,
  fill: color ? getFill(color, theme) : theme.colors.black
});

class Icon extends React.Component {
  render() {
    const Svg = SVG_COMPONENTS[this.props.type];

    const { label, className } = this.props;

    return <Svg className={className} label={label ? label : ''} />;
  }
}

Icon.propTypes = {
  className: PropTypes.string.isRequired,
  label: PropTypes.oneOfType([PropTypes.string, PropTypes.oneOf([false])])
    .isRequired,
  type: PropTypes.oneOf(iconTypes).isRequired,

  color: PropTypes.oneOf([
    'default',
    'primary',
    'success',
    'warning',
    'danger',
    'black',
    'white'
  ]),

  size: PropTypes.oneOf(['1.2x', '1.5x', '2x', '2.5x', '3x', '3.5x', '4x'])
};

Icon.displayName = 'Icon';

export default createComponent(iconStyles, Icon);
