/**
 * Creates an object whose keys and values are equal, populated
 * from the list of keys provided
 * @param keys Keys to populate enumerable
 */
export function enumerable(keys) {
  const result = keys.reduce((result, key) => {
    result[key] = key;
    return result;
  }, {});

  result.is = k => k in result;

  return result;
}