import * as React from 'react';
import { IRenderer, IConfig } from 'fela';

interface ICreateRendererOptions extends IConfig {
  dev?: boolean;
}

interface IStyleProviderProps {
  renderer?: IRenderer;
}

export class StyleProvider extends React.Component<
  IStyleProviderProps & ICreateRendererOptions,
  Object
> {}

export function createRenderer(options?: ICreateRendererOptions): IRenderer;

export const felaTestContext: React.StatelessComponent<{
  component: React.ComponentClass | React.StatelessComponent;
  renderer: IRenderer;
}>;

export function felaSnapshot(
  component: React.ComponentClass
): {
  component: any;
  styles: string;
};
